mapper.insert:

  			SELECT MAX(notice_code)
  				FROM notice

  		INSERT 
  			INTO notice
  			VALUES (


					1


					(#{notice_code} + 1)


				,#{member_id}
				,#{notice_subject}
				,#{notice_content}
				,#{notice_readcount}
				,now()
  			)

mapper.update:
UPDATE notice
	SET
		notice_subject = #{notice_subject}
		,notice_content = #{notice_content}
	WHERE
		notice_code = #{notice_code}

mapper.select:
SELECT *
	FROM review
	WHERE
		pro_code = #{pro_code}

		AND review_subject LIKE '%${keyword}%'

ORDER BY
	review_re_ref DESC, review_re_seq ASC
LIMIT 
	#{startRow}
	, #{listLimit}

mapper.insert:

  			SELECT MAX(review_code)
  				FROM review

		INSERT INTO review 
			VALUES (


					1


					(#{review_code} + 1)


				, #{member_id}
				, #{review_pass}
				, #{review_subject}
