mapper.insert:

  			SELECT MAX(notice_code)
  				FROM notice

  		INSERT 
  			INTO notice
  			VALUES (


					1


					(#{notice_code} + 1)


				,#{member_id}
				,#{notice_subject}
				,#{notice_content}
				,#{notice_readcount}
				,now()
  			)

mapper.update:
UPDATE notice
	SET
		notice_subject = #{notice_subject}
		,notice_content = #{notice_content}
	WHERE
		notice_code = #{notice_code}

mapper.insert:

  			SELECT MAX(qna_code)
  				FROM qna

  		INSERT 
  			INTO qna
  			VALUES (


					1


					(#{qna_code} + 1)


				,#{member_id}
				,#{qna_pass}
				,#{qna_subject}
				,#{qna_content}
				,#{qna_file}
				,#{qna_real_file}
				,#{qna_code} + 1
				,#{qna_re_lev}
				,#{qna_re_seq}
				,now()
				,#{qna_secret}
				,#{pro_code}
  			)

mapper.select:
SELECT *
	FROM review
	WHERE
		pro_code = #{pro_code}

		AND review_subject LIKE '%${keyword}%'

ORDER BY
	review_re_ref DESC, review_re_seq ASC
LIMIT 
	#{startRow}
	, #{listLimit}
