mapper.update:
UPDATE member
	SET member_pass = #{member_pass}
		, member_email = #{member_email}  
		, member_mobile = #{member_mobile}  
		, member_addr1 = #{member_addr1}  
		, member_addr2 = #{member_addr2}  
		, member_postcode = #{member_postcode}  
	WHERE
		member_id = #{member_id}

mapper.select:
SELECT COUNT(*)
	FROM orders
	WHERE member_id = #{id}

SELECT COUNT(*)
	FROM review
	WHERE member_id = #{id}

SELECT COUNT(*)
	FROM qna
	WHERE member_id = #{id}

